import React, { useRef } from 'react';
import { Upload, X } from 'lucide-react';

const VideoUploader = ({ selectedFile, onFileSelect, error }) => {
  const fileInputRef = useRef(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('video/')) {
      onFileSelect(file);
    } else {
      onFileSelect(null);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    const file = e.dataTransfer.files[0];
    if (file && file.type.startsWith('video/')) {
      onFileSelect(file);
    } else {
      onFileSelect(null);
    }
  };

  return (
    <div className="video-uploader">
      <div 
        className="video-uploader__dropzone"
        onDragOver={(e) => e.preventDefault()}
        onDrop={handleDrop}
        onClick={() => fileInputRef.current.click()}
      >
        <input
          ref={fileInputRef}
          type="file"
          onChange={handleFileChange}
          accept="video/*"
          className="video-uploader__input"
        />
        <Upload className="video-uploader__icon" />
        <p className="video-uploader__text">
          <span>Click to upload</span> or drag and drop
        </p>
        <p className="video-uploader__subtext">MP4, AVI, MOV (MAX. 800MB)</p>
      </div>

      {selectedFile && (
        <div className="video-uploader__file-info">
          <p>Selected file: {selectedFile.name}</p>
          <button onClick={() => onFileSelect(null)} className="video-uploader__remove-button">
            <X />
          </button>
        </div>
      )}

      {error && (
        <div className="video-uploader__error">
          <p>{error}</p>
        </div>
      )}
    </div>
  );
};

export default VideoUploader;
