import React from 'react';
import { Play, RefreshCw } from 'lucide-react';

const ProcessingControls = ({ selectedFile, processing, onProcessVideo, onReset, progress }) => {
  return (
    <div className="processing-controls">
      <div className="processing-controls__buttons">
        <button
          onClick={onProcessVideo}
          disabled={!selectedFile || processing}
          className="processing-controls__start-button"
        >
          {processing ? (
            <>
              <RefreshCw className="processing-controls__icon processing-controls__icon--spin" />
              Processing...
            </>
          ) : (
            <>
              <Play className="processing-controls__icon" />
              Start Processing
            </>
          )}
        </button>
        <button onClick={onReset} disabled={processing} className="processing-controls__reset-button">
          Reset
        </button>
      </div>

      {processing && (
        <div className="processing-controls__progress">
          <progress value={progress} max="100" className="processing-controls__progress-bar" />
          <p className="processing-controls__progress-text">Processing: {progress}%</p>
        </div>
      )}
    </div>
  );
};

export default ProcessingControls;
