import React, { useState, useCallback, useMemo } from 'react';
import VideoUploader from './VideoUploader';
import ProcessingControls from './ProcessingControls';
import ImageGallery from './ImageGallery';
import { useVideoProcessing } from './hooks/useVideoProcessing';
import '../../styles/VideoProcessor.css';

const VideoProcessor = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const { processing, progress, images, error, processVideo, reset } = useVideoProcessing();

  const handleFileSelect = useCallback((file) => {
    setSelectedFile(file);
  }, []);

  const handleProcessVideo = useCallback(() => {
    processVideo(selectedFile);
  }, [selectedFile, processVideo]);

  const memoizedImageGallery = useMemo(() => (
    <ImageGallery images={images} />
  ), [images]);

  return (
    <div className="video-processor">
      <h2 className="video-processor__title">Video to Images Converter</h2>
      <VideoUploader 
        selectedFile={selectedFile} 
        onFileSelect={handleFileSelect}
        error={error}
      />
      <ProcessingControls 
        selectedFile={selectedFile}
        processing={processing}
        onProcessVideo={handleProcessVideo}
        onReset={reset}
        progress={progress}
      />
      {memoizedImageGallery}
    </div>
  );
};

export default React.memo(VideoProcessor);
